mapper.select:
SELECT bookshelfno, m_no, p_name, p_infor, file1, thumb1, size1
FROM bookshelf
WHERE bookshelfno = #{bookshelfno}

mapper.update:
UPDATE bookshelf
SET p_name=#{p_name}, p_infor=#{p_infor}
WHERE bookshelfno = #{bookshelfno}

UPDATE bookshelf
SET file1=#{file1}, thumb1=#{thumb1}, size1=#{size1}
WHERE bookshelfno=#{bookshelfno}
