mapper.select:
SELECT COUNT(id) as cnt
FROM member
WHERE id=#{id}

SELECT m_no, id, passwd, mname, tel, email, zipcode, address1, address2, mdate
FROM member
WHERE m_no = #{m_no}

SELECT m_no, id, passwd, mname, tel, email, zipcode, address1, address2, mdate
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member 
SET mname=#{mname}, email=#{email}, tel=#{tel}, zipcode=#{zipcode},
      address1=#{address1}, address2=#{address2}
WHERE m_no=#{m_no}

mapper.select:
SELECT COUNT(m_no) as cnt
FROM member
WHERE m_no=#{m_no} AND passwd=#{passwd}

mapper.update:
UPDATE member
SET passwd=#{passwd}
WHERE m_no=#{m_no}

mapper.select:
SELECT COUNT(m_no) as cnt
FROM member
WHERE id=#{id} AND passwd=#{passwd}
