mapper.select:
SELECT orderno, m_no, p_number, p_infor, p_amount, p_state, file1, thumb1, size1
FROM memberph
WHERE orderno = #{orderno}

mapper.update:
UPDATE memberph
SET p_number=#{p_number}, p_infor=#{p_infor} , p_amount =#{p_amount}, p_state=#{p_state}
WHERE orderno = #{orderno}

UPDATE memberph
SET file1=#{file1}, thumb1=#{thumb1}, size1=#{size1}
WHERE orderno=#{orderno}
